function createGUID() {
    var d = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
        return v.toString(16);
    });
    return d;
}

function createAnonUserGuid() {
    var old = getCookie("uid-s");
    if (old != null && old !== "")
        return old;
    var guid = "$" + createGUID().substring(1);
    setCookie("uid-s", guid, 100);
    return guid;
}
